import React, {useState} from 'react'

const App = () => {
  const [score, setScore] = useState(0)
  const [currentQuestion, setCurrentQuestion] = useState(0)

  const questions = [
    {
      text: 'Question 1: This is the first question.',
      type: 'agree',
    },
    {
      text: 'Question 2: This is the second question.',
      type: 'disagree',
    },
    // Add more questions here
  ]

  const handleAnswer = answerType => {
    const currentQuestionType = questions[currentQuestion].type

    if (currentQuestionType === answerType) {
      setScore(score + 1)
    }

    if (currentQuestion === questions.length - 1) {
      // Last question reached, quiz is over
      alert(`Quiz completed! Your final score is ${score}/${questions.length}`)
    } else {
      setCurrentQuestion(currentQuestion + 1)
    }
  }

  return (
    <div className='quiz-container'>
      <h1>Quiz</h1>
      {currentQuestion < questions.length ? (
        <div>
          <p>{questions[currentQuestion].text}</p>
          <div className='answer-options'>
            <button onClick={() => handleAnswer('agree')}>Agree</button>
            <button onClick={() => handleAnswer('disagree')}>Disagree</button>
          </div>
          <p>Score: {score}</p>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  )
}

export default App
