    type: 'agree',
  },
  {
    text: "Question 98: I've learned not to expect someone else to be very concerned about my welfare.",
    type: 'agree',
  },
  {
    text: "Question 99: I don't look upon the past with any regrets.",
    type: 'agree',
  },
  {
    text: "Question 100: I can't feel really content unless I'm relaxed and doing nothing.",
    type: 'disagree',
  },
]

const App = () => {
  const [scores, setScores] = useState(new Array(10).fill(0))
  const [currentQuestion, setCurrentQuestion] = useState(0)
  const [showScore, setShowScore] = useState(false)

  const handleAnswer = answerType => {
    const currentQuestionType = questions[currentQuestion].type
    const currentQuestionNumber = currentQuestion + 1

    if (currentQuestionType === answerType) {
      const secondDigit = Number(
        String(currentQuestionNumber).charAt(currentQuestionNumber < 10 ? 0 : 1)
      )
      var currentScores = scores
      currentScores[secondDigit - 1] = currentScores[secondDigit - 1] + 1
      setScores(currentScores)
    }

    if (currentQuestion === questions.length - 1) {
      setShowScore(true)
    } else {
      setCurrentQuestion(currentQuestion + 1)
    }
  }

  const handleRestartQuiz = () => {
    setCurrentQuestion(0)
    setScores(new Array(10).fill(0))
    setShowScore(false)
  }

  return (
    <div className='quiz-container'>
      <h1>Beliefs Inventory</h1>
      <p className='desc'>
        It is not necessary to ponder over any item very long. Check off your answer quickly and go
        on to the next statement. Be sure to mark how you actually think about the statement, not
        how you think you should think.
      </p>
      {showScore ? (
        <div>
          <p className='question'>Quiz completed! Your final scores are {scores}</p>
          <button className='option-btn' onClick={handleRestartQuiz}>
            Take Again
          </button>
        </div>
      ) : (
        <>
          <p className='question'> {questions[currentQuestion].text}</p>
          <div className='answer-options'>
            <button className='option-btn' onClick={() => handleAnswer('agree')}>
              Agree
            </button>
            <button className='option-btn' onClick={() => handleAnswer('disagree')}>
              Disagree
            </button>
          </div>
        </>
      )}
    </div>
  )
}

export default App
